import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  or: 0,
  bois: 0,
  nourriture: 0,
  pierre: 0,
  metal: 0,
  energie: 0,
};

const resourceSlice = createSlice({
  name: 'resources',
  initialState,
  reducers: {
    updateResources(state, action) {
      return { ...state, ...action.payload };
    },
    setInitialResources(state, action) {
      return { ...state, ...action.payload };
    },
  },
});

export const { updateResources, setInitialResources } = resourceSlice.actions;
export default resourceSlice.reducer;
