import React, { useEffect, useState } from 'react';
import axiosInstance from '../utils/axiosInstance';
import './DefenseDetail.css';

const DefenseDetail = ({ building }) => {
  const [buildingDetails, setBuildingDetails] = useState(null);

  useEffect(() => {
    const fetchBuildingDetails = async () => {
      try {
        const response = await axiosInstance.get(`/api/defense/defense-buildings/${building.id}`);
        setBuildingDetails(response.data);
      } catch (error) {
        console.error("Error fetching building details: ", error);
      }
    };

    fetchBuildingDetails();
  }, [building]);

  const handleUpgrade = async () => {
    try {
      await axiosInstance.post(`/api/defense/defense-buildings/${building.id}/upgrade`);
      // Refresh the building details after upgrade
      const response = await axiosInstance.get(`/api/defense/defense-buildings/${building.id}`);
      setBuildingDetails(response.data);
    } catch (error) {
      console.error("Error upgrading building: ", error);
    }
  };

  const handleDestroy = async () => {
    try {
      await axiosInstance.post(`/api/defense/defense-buildings/${building.id}/destroy`);
      // Handle post-destroy actions, e.g., remove the building from the list
    } catch (error) {
      console.error("Error destroying building: ", error);
    }
  };

  if (!buildingDetails) return <p>Loading...</p>;

  return (
    <div className="defense-detail">
      <h2>{buildingDetails.name}</h2>
      <p>{buildingDetails.description}</p>
      <p>Quantity: {buildingDetails.quantity}</p>
      <button onClick={handleUpgrade}>Upgrade</button>
      <button onClick={handleDestroy}>Destroy</button>
    </div>
  );
};

export default DefenseDetail;
