// frontend/src/components/ResourcesWidget.js
import React, { useEffect, useState } from 'react';
import './ResourcesWidget.css';

const ResourcesWidget = () => {
  const [resources, setResources] = useState({
    or: 0,
    bois: 0,
    nourriture: 0,
    pierre: 0,
    metal: 0,
    energie: 0,
  });

  useEffect(() => {
    const ws = new WebSocket('ws://localhost:5000');

    ws.onopen = () => {
      console.log('WebSocket connection opened');
      // Envoie un message au serveur pour indiquer que l'utilisateur est connecté
      ws.send(JSON.stringify({ event: 'user_connected', userId: 1 })); // Remplacez 1 par l'ID de l'utilisateur réel
    };

    ws.onmessage = (event) => {
      const updatedResources = JSON.parse(event.data);
      console.log('Received resources:', updatedResources);
      const userResources = updatedResources.reduce((acc, resource) => {
        acc[resource.type] = resource.amount;
        return acc;
      }, {});
      setResources(userResources);
    };

    ws.onclose = () => {
      console.log('WebSocket connection closed');
    };

    ws.onerror = (error) => {
      console.error('WebSocket error:', error);
    };

    return () => {
      ws.send(JSON.stringify({ event: 'user_disconnected', userId: 3 })); // Remplacez 1 par l'ID de l'utilisateur réel
      ws.close();
    };
  }, []);

  return (
    <div className="resources-widget">
      <div className="resource-item">
        <img src="/images/resources/or.png" alt="Or" />
        Or: {resources.or}
      </div>
      <div className="resource-item">
        <img src="/images/resources/bois.png" alt="Bois" />
        Bois: {resources.bois}
      </div>
      <div className="resource-item">
        <img src="/images/resources/nourriture.png" alt="Nourriture" />
        Nourriture: {resources.nourriture}
      </div>
      <div className="resource-item">
        <img src="/images/resources/pierre.png" alt="Pierre" />
        Pierre: {resources.pierre}
      </div>
      <div className="resource-item">
        <img src="/images/resources/metal.png" alt="Metal" />
        Metal: {resources.metal}
      </div>
      <div className="resource-item">
        <img src="/images/resources/energie.png" alt="Energie" />
        Energie: {resources.energie}
      </div>
    </div>
  );
};

export default ResourcesWidget;
