import React, { useEffect, useState } from 'react';
import Menu from './Menu';
import axiosInstance from '../utils/axiosInstance';
import './Defense.css';
import DefenseDetail from './DefenseDetail';

const Defenses = () => {
  const [data, setData] = useState([]);
  const [selectedBuilding, setSelectedBuilding] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axiosInstance.get('/api/defenses/defense-buildings');
        console.log('Fetched data:', response.data);
        setData(response.data);
      } catch (error) {
        console.error('Error fetching defense buildings:', error);
        setData([]); // En cas d'erreur, on définit data comme un tableau vide pour éviter les erreurs
      }
    };
    fetchData();
  }, []);

  const handleBuildingClick = (building) => {
    if (selectedBuilding && selectedBuilding.id === building.id) {
      setSelectedBuilding(null); // Désélectionne le bâtiment si déjà sélectionné
    } else {
      setSelectedBuilding(building);
    }
  };

  return (
    <div className="defenses-container">
      <Menu />
      <div className={`defenses-content ${selectedBuilding ? 'with-details' : ''}`}>
        <h1>Defenses</h1>
        {selectedBuilding && (
          <DefenseDetail building={selectedBuilding} />
        )}
        <div className="defenses-list">
          {Array.isArray(data) && data.map((building) => (
            <div key={building.id} className="defenses-card" onClick={() => handleBuildingClick(building)}>
              <img
                src={`/images/defense/${building.name.toLowerCase().replace(' ', '_')}.png`}
                alt={building.name}
                className="defenses-image"
              />
              <h3>{building.name}</h3>
              <p>Quantity: {building.quantity}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Defenses;
