import React, { useEffect, useState } from 'react';
import Menu from './Menu';
import axiosInstance from '../utils/axiosInstance';
import './Research.css';
import ResearchDetail from './ResearchDetail';

const Research = () => {
  const [data, setData] = useState([]);
  const [selectedResearch, setSelectedResearch] = useState(null);

  const allowedResearches = [
    'Advanced Extraction', 'Intensive Agriculture', 'Resource Management', 'Renewable Energy',
    'Automated Defense System', 'Advanced Weaponry', 'Intensive Training', 'Rapid Communication',
    'Medical Technology', 'Reinforced Materials', 'Advanced Camouflage', 'Rapid Transport',
    'Surveillance Systems', 'Laser Technology', 'Robotic Production', 'Energy Shields',
    'Commando Training', 'Tunnel Networks', 'Crisis Management', 'Nanobot Technology'
  ];

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axiosInstance.get('/research/research-items');
        const filteredData = response.data.filter(research => allowedResearches.includes(research.name));
        setData(filteredData);
      } catch (error) {
        console.error('Error fetching research items:', error);
        setData([]); // Set data to an empty array on error
      }
    };
    fetchData();
  }, []);

  const handleResearchClick = (research) => {
    if (selectedResearch && selectedResearch.id === research.id) {
      setSelectedResearch(null); // Deselect if already selected
    } else {
      setSelectedResearch(research);
    }
  };

  return (
    <div className="research-container">
      <Menu />
      <div className={`research-content ${selectedResearch ? 'with-details' : ''}`}>
        <h1>Research</h1>
        {selectedResearch && (
          <ResearchDetail research={selectedResearch} />
        )}
        <div className="research-list">
          {Array.isArray(data) && data.map((research) => (
            <div key={research.id} className="research-card" onClick={() => handleResearchClick(research)}>
              <img
                src={`/images/research/${research.name.toLowerCase().replace(' ', '_')}.png`}
                alt={research.name}
                className="research-image"
              />
              <h3>{research.name}</h3>
              <p>Level: {research.level}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Research;
