import React, { useEffect, useState } from 'react';
import Menu from './Menu';
import axiosInstance from '../utils/axiosInstance';
import './Defense.css';
import DefenseDetail from './DefenseDetail';

const Defenses = () => {
  const [data, setData] = useState([]);
  const [selectedDefense, setSelectedDefense] = useState(null);

  const allowedDefenses = ['Artillery', 'Cannon', 'Wall', 'Missile Defense', 'Laser Turret'];

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axiosInstance.get('/api/defense/defense-buildings');
        const filteredData = response.data.filter(defense => allowedDefenses.includes(defense.name));
        setData(filteredData);
      } catch (error) {
        console.error('Error fetching defense buildings:', error);
        setData([]); // Set data to an empty array on error
      }
    };
    fetchData();
  }, []);

  const handleDefenseClick = (defense) => {
    if (selectedDefense && selectedDefense.id === defense.id) {
      setSelectedDefense(null); // Deselect if already selected
    } else {
      setSelectedDefense(defense);
    }
  };

  const formatDefenseName = (name) => {
    return name.toLowerCase().replace(/\s+/g, '_').normalize('NFD').replace(/[\u0300-\u036f]/g, ''); // Replace spaces with underscores and remove accents
  };

  return (
    <div className="defense-container">
      <Menu />
      <div className={`defense-content ${selectedDefense ? 'with-details' : ''}`}>
        <h1>Defense</h1>
        {selectedDefense && (
          <DefenseDetail defense={selectedDefense} />
        )}
        <div className="defense-list">
          {Array.isArray(data) && data.map((defense) => (
            <div key={defense.id} className="defense-card" onClick={() => handleDefenseClick(defense)}>
              <img
                src={`/images/defense/${formatDefenseName(defense.name)}.png`}
                alt={defense.name}
                className="defense-image"
              />
              <h3>{defense.name}</h3>
              <p>Quantity: {defense.quantity}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Defenses;
