import React, { useEffect, useState } from 'react';
import axiosInstance from '../utils/axiosInstance';
import './ResourcesWidget.css';

const ResourcesWidget = () => {
  const [resources, setResources] = useState({
    or: 0,
    bois: 0,
    nourriture: 0,
    pierre: 0,
    metal: 0,
    energie: 0,
  });

  useEffect(() => {
    const fetchResources = async () => {
      try {
        const response = await axiosInstance.get('/resources/user-resources');
        const userResources = response.data.reduce((acc, resource) => {
          acc[resource.type] = resource.amount;
          return acc;
        }, {});
        setResources(userResources);
      } catch (error) {
        console.error('Error fetching resources:', error);
      }
    };

    fetchResources();

    const interval = setInterval(() => {
      fetchResources();
    }, 10000); // Update every 10 seconds

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="resources-widget">
      <div className="resource-item">Or: {resources.or}</div>
      <div className="resource-item">Bois: {resources.bois}</div>
      <div className="resource-item">Nourriture: {resources.nourriture}</div>
      <div className="resource-item">Pierre: {resources.pierre}</div>
      <div className="resource-item">Metal: {resources.metal}</div>
      <div className="resource-item">Energie: {resources.energie}</div>
    </div>
  );
};

export default ResourcesWidget;
